// Enhanced connection pooling and request monitoring
let failedRequests = 0;
const maxRetries = 5;
const connectionPool = new Set();
const maxPoolSize = 100; // Increased from 50 to 100

// Simplified handleRequestWithRetry function to avoid excessive type instantiation
const handleRequestWithRetry = async (requestFn) => {
  let retries = 0;
  
  while (retries < maxRetries) {
    try {
      if (connectionPool.size >= maxPoolSize) {
        const delay = 50 * Math.pow(2, retries);
        await new Promise(resolve => setTimeout(resolve, delay));
      }
      
      const requestId = Date.now() + Math.random();
      connectionPool.add(requestId);
      
      const result = await requestFn();
      
      connectionPool.delete(requestId);
      return result;
    } catch (error) {
      retries++;
      if (retries >= maxRetries) {
        console.error('Maximum retries reached for Supabase request');
        throw error;
      }
      
      const baseDelay = 200 * Math.pow(2, retries);
      const jitter = Math.random() * 300;
      await new Promise(resolve => setTimeout(resolve, baseDelay + jitter));
    }
  }
};

// Monitor for authentication failures
supabase.auth.onAuthStateChange((event, session) => {
  if (event === 'SIGNED_OUT') {
    console.log('User signed out');
    failedRequests = 0; // Reset counter on sign out
  } else if (event === 'USER_UPDATED') {
    console.log('User session updated');
  }
});

// Enhance original auth methods with retry logic
const originalSignIn = supabase.auth.signInWithPassword;
supabase.auth.signInWithPassword = async (credentials) => {
  return handleRequestWithRetry(async () => {
    try {
      const response = await originalSignIn(credentials);
      if (response.error) {
        failedRequests++;
        if (failedRequests > 5) {
          console.error('Multiple failed login attempts detected');
          setTimeout(() => { failedRequests = 0; }, 15 * 60 * 1000);
        }
      } else {
        failedRequests = 0;
      }
      return response;
    } catch (error) {
      console.error('Supabase auth error:', error);
      throw error;
    }
  });
};

// Add optimized batch operations for handling large datasets
export const batchOperation = async (items, operationFn, batchSize = 500) => {
  const batches = [];
  
  for (let i = 0; i < items.length; i += batchSize) {
    batches.push(items.slice(i, i + batchSize));
  }
  
  const results = [];
  for (const batch of batches) {
    try {
      const result = await operationFn(batch);
      results.push(result);
    } catch (error) {
      console.error('Batch operation failed:', error);
      throw error;
    }
  }
  
  return results;
};

// Optimized fetchPaginated function with NO size limits for truly unlimited data access
export const fetchPaginated = async <T>(
  tableName: 'admins' | 'voters',
  options: {
    filters?: Record<string, any>;
    orderBy?: string;
    ascending?: boolean;
  } = {}, 
  // Use a much larger page size to reduce number of requests
  pageSize = 20000
): Promise<T[]> => {
  const { filters = {}, orderBy = 'created_at', ascending = true } = options;
  let page = 0;
  let hasMore = true;
  const allResults: T[] = [];
  
  // Much higher maxPages to support virtually unlimited datasets
  const maxPages = 10000;
  
  console.log(`Starting unlimited paginated fetch for ${tableName}`);

  try {
    while (hasMore && page < maxPages) {
      const start = page * pageSize;
      
      let query = supabase.from(tableName).select('*', { count: 'exact' });
      
      // Add ordering
      query = query.order(orderBy, { ascending });
      
      // Add range for current page
      query = query.range(start, start + pageSize - 1);
      
      // Apply filters
      Object.entries(filters).forEach(([key, value]) => {
        if (value !== undefined && value !== null && value !== '') {
          // @ts-ignore - Ignore the typing here
          query = query.eq(key, value);
        }
      });

      console.log(`Fetching ${tableName} page ${page + 1} (${start}-${start + pageSize - 1})`);
      const { data, error, count } = await query;
      
      if (error) {
        console.error(`Error fetching paginated data (page ${page + 1}):`, error);
        throw error;
      }
      
      if (data && data.length > 0) {
        console.log(`Received ${data.length} records for page ${page + 1}`);
        allResults.push(...data as T[]);
        
        // Check if we've reached the total count
        if (typeof count === 'number' && allResults.length >= count) {
          console.log(`Fetched all ${count} records in ${page + 1} pages`);
          hasMore = false;
          break;
        }
      } else {
        console.log(`No data received for page ${page + 1}, stopping pagination`);
        hasMore = false;
      }
      
      // Only continue if we received a full page of results
      hasMore = data && data.length === pageSize;
      page++;
    }
    
    if (page >= maxPages) {
      console.warn(`Reached maximum page limit (${maxPages}) for ${tableName}`);
    }
    
    console.log(`Completed fetch with ${allResults.length} total records`);
    return allResults;
  } catch (error) {
    console.error('Error in fetchPaginated:', error);
    throw error;
  }
};
