// Add optimized batch operations for handling large datasets
export const batchOperation = async (items, operationFn, batchSize = 100) => {
  const batches = [];
  
  for (let i = 0; i < items.length; i += batchSize) {
    batches.push(items.slice(i, i + batchSize));
  }
  
  const results = [];
  for (const batch of batches) {
    try {
      const result = await operationFn(batch);
      results.push(result);
    } catch (error) {
      console.error('Batch operation failed:', error);
      throw error;
    }
  }
  
  return results;
};

// Add function for paginated fetches to handle large datasets with proper typing
export const fetchPaginated = async <T>(
  tableName: 'admins' | 'voters', // Fix: restrict to actual table names
  options: {
    filters?: Record<string, any> | undefined;
    orderBy?: string | undefined;
    ascending?: boolean | undefined;
  } = {}, 
  pageSize = 500
): Promise<T[]> => {
  const { filters = {}, orderBy = 'created_at', ascending = true } = options;
  let page = 0;
  let hasMore = true;
  const allResults: T[] = [];

  while (hasMore) {
    const start = page * pageSize;
    
    let query = supabase
      .from(tableName)
      .select('*', { count: 'exact' })
      .order(orderBy, { ascending })
      .range(start, start + pageSize - 1);
    
    // Apply any filters with proper typing
    Object.entries(filters).forEach(([key, value]) => {
      if (value !== undefined && value !== null && value !== '') {
        // Use type assertion here to resolve the type issue
        query = query.eq(key, value);
      }
    });

    const { data, error, count } = await query;
    
    if (error) {
      console.error('Error fetching paginated data:', error);
      throw error;
    }
    
    if (data && data.length > 0) {
      allResults.push(...data as T[]);
    }
    
    hasMore = data && data.length === pageSize;
    page++;
    
    // Safety measure to prevent infinite loops
    if (page > 100) {
      console.warn('Pagination safety limit reached');
      hasMore = false;
    }
  }
  
  return allResults;
};
